<?phpnamespace App\Models;use Illuminate\Database\Eloquent\Factories\HasFactory;use Illuminate\Database\Eloquent\Model;use Illuminate\Database\Eloquent\SoftDeletes;class AssignDispatch extends Model{    use HasFactory, SoftDeletes;	protected $fillable = [        'user_id','dispatch_id','vehicle_id','no_of_vehicles','no_of_vehicles_provide','user_type','status','amount_paid','created_by','updated_by','decline_reason','accepted_decline_date','complete_date','push_notification_sent','push_notification_message_id','notification_id','push_notification_message','vehicle_number','user_name','contact_number'];		public function getUser(){        return $this->belongsTo(User::class,'user_id');    }	public function getDispatch(){        return $this->belongsTo(Dispatch::class,'dispatch_id');    }	public function getVehicle(){        return $this->belongsTo(Vehicle::class,'vehicle_id');    }	public function getBrokerVehicle(){        return $this->hasMany(AssignDispatchBrokerVehicle::class,'assign_dispatch_id');    }	public function getDispatchTicket(){        return $this->hasOne(DispatchTicket::class,'assign_dispatch_id');    }}